middleware = applicationContext.get_component(componentId = "filterChainProxy") middleware.application = wsgiApp
# Start the CherryPy server. cherrypy.server.start(middleware=[middleware])
if options.irc: # This runs the IRC bot that connects to a channel and then responds to various events. applicationContext = CoilyIRCServer() coily = applicationContext.bot() coily.service.start()
